import React, { useState } from 'react'
import Axios from 'axios'
import { Container, Row, Col, Card, Form, Button, Alert } from 'react-bootstrap';

function Register() {
    const url ='http://localhost:3002/api'
    const [data, setData] = useState({
        name: '',
        email: ''
    })

    function submit(e) {
        e.preventDefault()
        Axios.post(url, {
            name: data.name,
            email: data.email
        })
        .then(res => {
            console.log(res.data)
        })
    }

    function handleSubmit(e) {
        const newData = {...data}
        newData[e.target.id] = e.target.value
        setData(newData)
        console.log(newData)
    }
    return (
        <>
            <Container>
            <Row>
            <Col xs={6}>
            <Card className="mt-5">
            <Card.Header>Login Form</Card.Header>
            <Card.Body>
                    <Form onSubmit={(e) => submit(e)}>
                        <input onChange={(e) => handleSubmit(e)} id='name' value={data.name} placeholder='name' type='text'></input>
                        <input onChange={(e) => handleSubmit(e)} id='email' value={data.email} placeholder='email' type='text'></input>
                        <button>Register!</button>
                    </Form>
            </Card.Body>
            </Card>
            </Col>
            </Row>
            </Container>

        </>
    )
}

export default Register
