import React, { useState } from 'react'
import Axios from 'axios'
import { Row, Col, Card, Form, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css'
import './Register.css'
import golf from '../images/background2.jpg'

function Register() {
    const [errors, setErrors] = useState({})

    const url ='https://615876535167ba00174bbb0e.mockapi.io/api/register'
    const [data, setData] = useState({
        name: '',
        email: '',
        phoneNum: '',
        shirt: '',
        handicap: ''
    })

    function submit(e) {
        e.preventDefault()
        const openErrors = errorParse()
        if (Object.keys(openErrors).length > 0) {setErrors(openErrors)} else {

        Axios.post(url, {
            name: data.name,
            email: data.email,
            phoneNum: data.phoneNum,
            shirt: data.shirt,
            handicap: data.handicap
        })
        .then(res => {
            console.log(res.data)
            setData({
                name: '',
                email: '',
                phoneNum: '',
                shirt: '',
                handicap: ''
            })
            alert('Thanks for registering!')
        })
    }
}

    function handleSubmit(e) {
        const newData = {...data}
        newData[e.target.id] = e.target.value
        setData(newData)
        console.log(newData)

    }
    function errorParse() {
        const {name, email, phoneNum, shirt, handicap} = data
        const openErrors = {}
        if (!name || name === '') {openErrors.name = 'You must enter a name!'}
        if (!email || email === '') {openErrors.email = 'You must enter a valid email'}
        if (phoneNum.length < 7 || phoneNum.length > 7 ) {openErrors.phoneNum = 'You must enter a 7 digit phone number!'}
        if (!shirt || shirt === '' ) {openErrors.shirt = 'You select a shirt size!'}
        if (handicap === '')  {openErrors.handicap = 'You enter a handicap'}
        return openErrors

    }
    return (
        <>
            <div style={{ background: `url(${golf})`, backgroundSize: 'cover', backgroundPosition: 'bottom center', width:'100%'}} className='middle'>
            <Row className='tag'>
            <Col>
            <h4 style={{color: 'white'}}>Please fill out the form below to register!</h4>
            <Card style={{ width: '25rem', height:'28rem' }}>
            <Card.Header>Registration</Card.Header>
            <Card.Body>
                    <Form>
                    <Form.Group>
                    <Form.Label htmlFor='name'></Form.Label>
                        <Form.Control 
                            onChange={handleSubmit}
                            id='name' 
                            value={data.name} 
                            placeholder='First and Last Name' 
                            type='text'
                            isInvalid={errors.name} />
                        <Form.Control.Feedback type='invalid'>
                            {errors.name}
                        </Form.Control.Feedback>
                    </Form.Group>
                    <Form.Group>
                    <Form.Label htmlFor='email'></Form.Label>
                        <Form.Control 
                            onChange={handleSubmit}
                            id='email' value={data.email} 
                            placeholder='Email Address' 
                            type='email'
                            isInvalid={!!errors.email} />
                        <Form.Control.Feedback type='invalid'>
                            {errors.email}
                        </Form.Control.Feedback>
                    </Form.Group>
                    <Form.Group>
                    <Form.Label htmlFor='phone'></Form.Label>
                        <Form.Control 
                        onChange={handleSubmit} 
                        id='phoneNum' value={data.phoneNum} 
                        placeholder='Phone Number' 
                        type='number'
                        isInvalid={!!errors.phoneNum} />
                    <Form.Control.Feedback type='invalid'>
                        {errors.phoneNum}
                    </Form.Control.Feedback>
                    </Form.Group>
                    <Form.Group>
                    <Form.Label htmlFor='shirtSize'></Form.Label>
                        <Form.Control 
                        as='select' 
                        onChange={handleSubmit} 
                        id='shirt' value={data.shirt} 
                        placeholder='Select a shirt size'
                        isInvalid={!!errors.shirt}>
                            <option value=''>Select a shirt size</option>
                            <option value='small'>Small</option>
                            <option value='medium'>Meidum</option>
                            <option value='large'>Large</option>
                            <option value='xl'>Extra Large</option>
                        </Form.Control>
                        <Form.Control.Feedback type='invalid'>
                            {errors.shirt}
                        </Form.Control.Feedback>
                    </Form.Group>
                    <Form.Group>
                    <Form.Label htmlFor='handicap'></Form.Label>
                        <Form.Control 
                        onChange={handleSubmit} 
                        id='handicap' value={data.handicap} 
                        placeholder='Golf Handicap' 
                        type='number' 
                        isInvalid={errors.handicap}/>
                        <Form.Control.Feedback type='invalid'>
                            {errors.handicap}
                        </Form.Control.Feedback>
                    </Form.Group>
                    <br></br>
                    <Form.Group>
                        <Button style={{background: 'rgba(43, 153, 226, 0.747)'}} onClick={submit}>Register!</Button>
                    </Form.Group>
                    </Form>
            </Card.Body>
            </Card>
            </Col>
            </Row>
            </div>

        </>
    )
}

export default Register
